		public static float WinningPercentage(Card firstCard, Card secondCard)
		{
			var value = firstCard.Suit == secondCard.Suit
						  ? (firstCard.Type > secondCard.Type
								 ? WINNING[MaxCardTypeValue - (int)firstCard.Type, MaxCardTypeValue - (int)secondCard.Type]
								 : WINNING[MaxCardTypeValue - (int)secondCard.Type, MaxCardTypeValue - (int)firstCard.Type])
						  : (firstCard.Type > secondCard.Type
								 ? WINNING[MaxCardTypeValue - (int)secondCard.Type, MaxCardTypeValue - (int)firstCard.Type]
								 : WINNING[MaxCardTypeValue - (int)firstCard.Type, MaxCardTypeValue - (int)secondCard.Type]);

			return value;
		}


